record.methods:
PROCEDURE Click
local result

select mr_mods_categories1.categoryid, ;
	mr_mods_categories1.categoryname, ;
	count(mr_mods_categories1.categoryname) as categorycount;
	from mr_mods_categories1;
	where mr_mods_categories1.iguid = thisform.mr_iguid;
	group by mr_mods_categories1.categoryid, ;
	mr_mods_categories1.categoryname;
	order by mr_mods_categories1.categoryname into cursor 'category_menu'

this.tbmenuitemadd(-1, 0, 0, thisform.mr_categoryid = -1, 0, 'All')

select 'category_menu'

scan for category_menu.categoryid > 0

	this.tbmenuitemadd(category_menu.categoryid, 0, 0, thisform.mr_categoryid = category_menu.categoryid, 0, category_menu.categoryname + ' (' + transform(category_menu.categorycount) + ')')

endscan

m.result = this.tbmenushow()

if m.result # 0

	thisform.mr_categoryid = m.result

	thisform.mr_setinstance()

endif




ENDPROC
